import React, { useState, useEffect, createContext } from 'react';
import Web3Modal from 'web3modal';
import {
    w3connect,
    providerOptions,
    createWeb3User
} from '../utils/Auth';
import { getChainData } from '../utils/Chains';

export const LoaderContext = createContext(false);
export const Web3ModalContext = createContext();
export const CurrentUserContext = createContext();

const Store = ({ children }) => {

    const [currentUser, setCurrentUser] = useState();
    const [loading, setLoading] = useState(false);
    const [web3Connect, setWeb3Modal] = useState(
        new Web3Modal.Core({
            network: getChainData(+process.env.REACT_APP_CHAIN_ID).network, // optional
            providerOptions, // required
            cacheProvider: true,
        }),
    );

    useEffect(() => {

        const initCurrentUser = async () => {
            let user;
            try {
                const w3c = await w3connect(
                    web3Connect,
                );
                const [account] = await w3c.web3.eth.getAccounts();
                setWeb3Modal(w3c);
                user = createWeb3User(account);
                setCurrentUser(user);
            } catch (e) {
                console.error(
                    `Could not log in with web3`,
                );
            }
        };
        if (web3Connect.cachedProvider) {
            initCurrentUser();
        }
    }, [web3Connect]);
    return (
        <LoaderContext.Provider value={[loading, setLoading]}>
            <Web3ModalContext.Provider value={[web3Connect, setWeb3Modal]}>
                <CurrentUserContext.Provider
                    value={[currentUser, setCurrentUser]}
                >
                    {children}
                </CurrentUserContext.Provider>
            </Web3ModalContext.Provider>
        </LoaderContext.Provider>
    );
};

export default Store;
